// items = [{ json: { ...usuarioSlack } }, ...]
const EXCLUDE = new Set([
  'a-chatgpt','aabogada','aanthropic','agemini','aimagen',
  'aeva','rebeca','aprecios','aricardo','slackbot'
]);

return items.filter(({ json }) => {
  const p = json.profile || {};
  const display = (p.display_name_normalized || '').toLowerCase();
  const real    = (p.real_name_normalized || '').toLowerCase();
  const name    = (json.name || '').toLowerCase();

  // Quitar usuarios borrados o bots reales (opcional)
  if (json.deleted || json.is_bot === true) return false;

  // Mantener solo los que NO estén en la lista de exclusión
  return !EXCLUDE.has(display) && !EXCLUDE.has(real) && !EXCLUDE.has(name);
});

